#!/bin/bash

source /edx/app/hadoop/pipeline/bin/activate

interval=2017-04-01-$(date +%Y-%m-%d)
overwritendays='2'

PYTHONPATH='/edx/app/hadoop/pipeline/local/lib/python2.7/site-packages/edx/analytics/tasks/insights' \
luigi --module location_per_course InsertToMysqlLastCountryPerCourseTask \
--overwrite-n-days $overwritendays \
--interval $interval


function locationuser()
{
PYTHONPATH='/edx/app/hadoop/pipeline/local/lib/python2.7/site-packages/edx/analytics/tasks/insights' luigi --module location_per_course $1  --overwrite-n-days $overwritendays --interval $interval
}


function lastcountry()
{
PYTHONPATH='/edx/app/hadoop/pipeline/local/lib/python2.7/site-packages/edx/analytics/tasks/insights' luigi --module location_per_course $1  --overwrite-n-days $overwritendays --interval $interval
}



