function problemresponse()
{
PYTHONPATH='/edx/app/hadoop/pipeline/local/lib/python2.7/site-packages/edx/analytics/tasks/insights' luigi --module problem_response $1 --marker $marker --output-root $outputroot --overwrite
}

function problemresponseworkflow()
{
PYTHONPATH='/edx/app/hadoop/pipeline/local/lib/python2.7/site-packages/edx/analytics/tasks/' luigi --module insights.problem_response $1 --remote-log-level "DEBUG" --marker $marker --output-root $outputroot --overwrite 
}

function latestproblem()
{
PYTHONPATH='/edx/app/hadoop/pipeline/local/lib/python2.7/site-packages/edx/analytics/tasks/' luigi --module insights.problem_response $1 --output-root $outputroot --overwrite --interval $interval --remote-log-level "DEBUG"
}

function problemlocation()
{
PYTHONPATH='/edx/app/hadoop/pipeline/local/lib/python2.7/site-packages/edx/analytics/tasks/' luigi --module insights.problem_response $1 --overwrite --remote-log-level "DEBUG"
}


